//import React from 'react'
import Login from './components/LogIn'
import Register from './components/Register'
import ToDoContainer from './components/Todo'
import Navigator from './components/Navigator'
//import Home from './components/Home'
import Home from './components/Home'
import ProjectWindow from './components/ProjectWindow'
import { useHistory } from "react-router-dom";
import React, { useState } from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect
} from "react-router-dom";




const App = (props) => {
  var homeFirstLoad = false
  var projectsFirstLoad = false
  const [Authenticated, setAuthenticated] = useState(false);
  const [userName, setUserName] = useState("User");
  const [userProjects, setUserProjects] = useState([]);
  const [projectJson, setProjectJson] = useState([]);

  function requireAuth ()
  {
    if(Authenticated){ 
      console.log("is Authenticated")
      return <Redirect to="/Home"/>
    }
    else
    {
      console.log("hello ther")
      return <Redirect to="/"/>
    }
  }

  return(

    <div>
    
    <Router>    
    <Navigator></Navigator>
        <Switch>
         
        <Route exact path="/Login">
          <Login setAuto={setAuthenticated} setUserNameFunc = {setUserName} setUserProjectsFunc = {setUserProjects}/>
        </Route>  
      
        <Route path="/ToDo" render={requireAuth}></Route>
        <Route path="/Home" render={()=>{return(<Home userName={userName} userProjects = {userProjects} setProjectJson = {setProjectJson}/>)}}></Route>
        <Route path="/ProjectWindow" render={()=>{
          if(Authenticated )
          {
            if(!projectsFirstLoad)
            {
              console.log("is Authenticated but not loaded")
              projectsFirstLoad = true
              return(<ProjectWindow userName={userName} projectJson={projectJson} />)
            }
            else
            {
              console.log("is Authenticated but already loaded")
              projectsFirstLoad = false
              return <Redirect to="/"/>
            }
          }
            
          else
          {
            console.log("is not Authenticated")
            return <Redirect to="/"/>
          }
 
          }}></Route>
        <Route path="/Register" render={()=>{return(<Register setUserNameFunc={setUserName}/>)}}></Route>
        <Route path="/" ><Redirect to="/Login"/></Route>
          
      </Switch>
    </Router>

    </div>
    
    )
  
}

export default App
