//import React from 'react'
import Login from './components/LogIn'
import Register from './components/Register'
import ToDoContainer from './components/Todo'
import Navigator from './components/Navigator'
//import Home from './components/Home'
import Home from './components/Home2'
import { useHistory } from "react-router-dom";
import React, { useState } from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect
} from "react-router-dom";




const App = (props) => {
  const [Authenticated, setAuthenticated] = useState(false);
  const [userName, setUserName] = useState("User");
  const [userProjects, setUserProjects] = useState([]);

  function requireAuth ()
  {
    if(Authenticated){ 
      return <Redirect to="/Home"/>
    }
    else
    {
      return <Redirect to="/LogIn"/>
    }
  }

  return(

    <div>
      
    <Router>    
    <Navigator></Navigator>
        <Switch>
         
        <Route exact path="/Login">
          <Login setAuto={setAuthenticated} setUserNameFunc = {setUserName} setUserProjectsFunc = {setUserProjects}/>
        </Route>  
      
        <Route path="/ToDo" render={requireAuth}></Route>
        <Route path="/Home" render={()=>{return(<Home userName={userName} userProjects = {userProjects}/>)}}></Route>
        <Route path="/Register" render={()=>{return(<Register setUserNameFunc={setUserName}/>)}}></Route>
        <Route path="/" ><Redirect to="/Login"/></Route>
          
      </Switch>
    </Router>

    </div>
    
    )
  
}

export default App
