import { Table,Card, Form, Button, Container, Row, Col, Badge, Alert, Tab, Tabs, ListGroup, Accordion, Modal } from 'react-bootstrap'
import React, { list,useRef,useState, useEffect ,Sonnet } from 'react';
const sc=require("../services/serverCom.js");


const ProjectWindow = ({userName, projectJson}) => {

    console.log("ProjectWindow:")
    console.log(userName)
    console.log(projectJson)
    //members
    const [formValidated, setFormValidated] = useState(false);
    const [show, setShow] = useState(false);

    //functions
    const handleClose = () => 
    {
        setFormValidated(false);
        setShow(false);
    }
    const handleShow = () => setShow(true);
    const addIssue = () =>{
        handleShow();
    }



    //classes for tabs view
    const Issues = (issues) => {
        const [issueList, setIssueList] = useState([]);
        const IssuesRow = ({id, desc, dueData, assigned, status}) => {
            let button
            if (status === "active"  ) {
                button =  <Button variant="success" >Complete</Button>
            } else {
                button =    <Button variant="secondary" disabled>Waiting Approval</Button>
            }

            return (    
                    <tr>
                        <td>{id}</td>
                        <td>{desc}</td>
                        <td>{dueData}</td>
                        <td>{assigned}</td>
                        <td>
                            {button}
                        </td>
                    </tr>
            )}
        return (    
            <div >
                <Table striped bordered hover>
                    <thead>
                        <tr>
                        <th>ID</th>
                        <th>Description</th>
                        <th>Due Date</th>
                        <th>Assigned By</th>
                        <th>        </th>
                        </tr>
                    </thead>
                    <tbody>
                    {issueList.map(({ _id,desc, dueData, assigned, status}) => (
                        <IssuesRow id = {_id} desc = {desc} dueData = {dueData} assigned={assigned} status={status}></IssuesRow>
                    ))}
                    
                    </tbody>
                    </Table>
            </div>
            
            
    
        )}
       

    const Completed = () => {
        return (    
            <div className="home" >
                <div className="labelStyle" >Completed: </div>  
            </div>
            
    
        )}
    const Overdue = () => {
        return (    
            <div className="home" >
                <div className="labelStyle" >Overdue: </div>  
            </div>
            
    
        )}

    const Members = () => {
        return (    
            <div className="home" >
                <div className="labelStyle" >Members: </div>  
            </div>
            
    
        )}

    const Waiting = () => {
        return (    
            <div className="home" >
                <div className="labelStyle" >Waiting: </div>  
            </div>
            
    
        )}


    //render ProjectWindow
    return (    
        <div className='center'>
        <div>
            <div className="labelStyle" >Project: {projectJson.projectName} </div>
            <Button variant="outline-success" onClick={addIssue}>Add Issue</Button>
            <hr></hr>  
        </div>
            <Tabs defaultActiveKey="profile" id="uncontrolled-tab-example" className="mb-3">
            <Tab eventKey="issues" title="Issues">
                <Issues />   
            </Tab>
            <Tab eventKey="completed" title="Completed">
                <Completed />      
            </Tab>
            <Tab eventKey="overdue" title="Overdue">
                <Overdue />  
            </Tab>
            <Tab eventKey="members" title="Members">
                <Members />  
            </Tab>
            <Tab eventKey="waiting" title="Waiting for approval">
                <Waiting />  
            </Tab>

            </Tabs>
        </div>
        

    )}



export default ProjectWindow
