import { Card, Form, Button, Container, Row, Col, Badge, Alert, Tab, ListGroup, Accordion, Modal } from 'react-bootstrap'
import React, { useRef,useState, useEffect } from 'react';
//ref to Todo.js -> ListsComponent
const ProjectsList = () => {
    const projectsList = [
        { name: 'John', Description: '25/12/1990'},
        { name: 'Abby', Description: '22/10/1991'},
        { name: 'Liad', Description: '22/10/1991'},
        { name: 'Elroye', Description: '22/10/1991'},
        { name: 'Elroye', Description: '22/10/1991' }
    
      ];
    return (
        <Container className='ProjctsGrid'>
            
            <Row xs={1} md={2} className="g-4">
            {Array.from({ length: 4 }).map((_, idx) => (
                <Col>
                        <Card>
                    <Card.Img variant="top" src="holder.js/100px160" />
                    <Card.Body>
                        <Card.Title>Project Title</Card.Title>
                        <Card.Text>
                            Project Description
                        </Card.Text>
                        <Button variant="primary">Open</Button>
                    <Card.Title>Card title</Card.Title>
                    <Card.Text>
                        This is a longer card with supporting text below as a natural
                        lead-in to additional content. This content is a little bit longer.
                    </Card.Text>
                    </Card.Body>
                    </Card>
                </Col>
            ))}
            </Row>          
        </Container>

        
    );
}


const Home = ({ userName }) => {


    const [formValidated, setFormValidated] = useState(false);
    const [m_userName, setUserName] = useState(userName)
    const [show, setShow] = useState(false);
    const [projectName, setProjectName] = useState("");
    const handleClose = () => 
    {
        setFormValidated(false);
        setShow(false);
    }
    const handleShow = () => setShow(true);

    const addProject = () => 
    {
        handleShow();
    }

    const submitProject = (event)=>
    {
   
        event.preventDefault();
        const form = event.currentTarget
        const title = form.projectName.value
        const desc = form.projectDescription.value
        if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
            setFormValidated(true);
        }
   
        else
        {     
            handleClose();
            //add project to projects list
            //add project to dataBase
        }

  
    }
    //read projcets of user
    return (    
        <div >
        <div className="home">
            <div className="labelStyle">Hello, {m_userName}</div>
        </div>
        <div className="center">
            <div>
                <div id="projectName" className="labelStyle">My Projects</div>
                <Button variant="outline-success" onClick={addProject}>Add Project</Button>
                <hr></hr>
                
            </div>
         
            <ProjectsList ></ProjectsList>

            <Modal size="lg" aria-labelledby="contained-modal-title-vcenter" centered show={show} onHide={handleClose}>
                <Form noValidate validated={formValidated} onSubmit={submitProject}>
                    <Modal.Header closeButton>
                        <Modal.Title>Add Project</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>

                        {/* ProjectName */}
                        <Form.Group controlId="projectName">
                            <Form.Label>Projcet Name</Form.Label>
                            <Form.Control required type="text" placeholder={"Insert Name"} />
                            <Form.Control.Feedback type="valid"></Form.Control.Feedback>
                            <Form.Control.Feedback type="invalid">
                            Please enter a project description.
                            </Form.Control.Feedback>
                        </Form.Group>

                        {/* Description */}
                        <Form.Group controlId="projectDescription">
                            <Form.Label>Description</Form.Label>
                            <Form.Control as="textarea" rows={3} placeholder="Description" />   
                        </Form.Group>

                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                        <Button variant="primary" type="submit" onclick={submitProject}>
                            Add
                        </Button>

                    </Modal.Footer>
                </Form>
            </Modal>
        </div>
        </div>     
    )}
export default Home
