import React from 'react'



const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  const Header =() =>{
    return (

  <h1>{course}</h1>
    )
  }
  const Content= ()=>{
    return(
      <>
            <p>
        {part1} {exercises1}
      </p>
      <p>
        {part2} {exercises2}
      </p>
      <p>
        {part3} {exercises3}
      </p>
      </>
    )
  }

  const Total=()=>
  {
    return (
      <p>Number of exercises {exercises1 + exercises2 + exercises3}</p>
    )
  }
  return (
    <div>
      <Header />
      <Content />
    </div>
  )
}

export default App
