import { Table,Card, Form, Button, Container, Row, Col, Badge, Alert, Tab, Tabs, ListGroup, Accordion, Modal ,Dropdown} from 'react-bootstrap'
import React, { list,useRef,useState, useEffect ,Sonnet ,render} from 'react';


const sc=require("../services/serverCom.js");


const ProjectWindow = ({userName, projectJson}) => {
    var adminFound = false
    console.log("ProjectWindow:")
    console.log(userName)
    console.log(projectJson)


    //component members
    const [m_issuesList, setIssuesList] = useState(projectJson.issues);
    const [m_pendingList, setPedingList] = useState(projectJson.pending);
    const [m_adminList, setAdminList] = useState(projectJson.admin);
    const [m_memberList, setMemberList] = useState(projectJson.members);
    const [m_currentIssueID, setCurrentIssueID] = useState(projectJson.issues.length)
    


    //component global params
    const [formValidated, setFormValidated] = useState(false);
    const [showAddIssue, setAddIssue] = useState(false);
    const [showAddMember, setAddMember] = useState(false);
    const [newMemberPremission, setPremmisoin] = useState("Member");
    const [issueDateValid, setIssueDateValid] = useState(true);

    
    
    //init
    for (var index in projectJson.admin)
    {
        console.log(projectJson.admin[index])
        if(projectJson.admin[index] === userName)
        {
            adminFound = true
            console.log("found")
            break;
        }
    }

    let itemsList = []
    for (var index in projectJson.admin)
    {
        itemsList.push(projectJson.admin[index])
    }

    for (var index in projectJson.members)
    {
        itemsList.push(projectJson.members[index])
    }
    

    
    const [isAdmin, setIsAdmin] = useState(adminFound);

    const [dropDownItems, setDropDownItems] = useState(itemsList)

 
    
    //functions
    const successAddMemberToDB = (userName, premission, message) => {
        
        if(premission === "Admin")
        {
            const newList = [...m_adminList, userName];
            setAdminList(newList);
        }
        else
        {
            const newList = [...m_memberList, userName];
            setMemberList(newList);
        }   
        const newList = [... dropDownItems, userName]
        setDropDownItems(newList)
        alert(message)
    }
    
    const failAddMemberToDB = (message) =>{
        alert(message)
    }
    const closeMemberForm = () =>
    {
        setFormValidated(false);
        setPremmisoin("Member")
        setAddMember(false)
    }
    const submitMember = (event) =>{
        event.preventDefault();
        const form = event.currentTarget
        const memberUserName = form.memberUserName.value

        console.log("submitMember")
        console.log(memberUserName);
        console.log(newMemberPremission);

        if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
            setFormValidated(true);
        }
   
        else
        {      
            closeMemberForm()
            sc.addMember(memberUserName,projectJson.projectName, newMemberPremission, projectJson._id, successAddMemberToDB, failAddMemberToDB)
           

        }
        

    }

    const successAddIssue = () => {
        setCurrentIssueID(m_currentIssueID + 1)
    }
    
    const failedAddIssue = (message) =>{
        alert(message)
    }


    const submitIssue = (event) =>{
        event.preventDefault();
        const form = event.currentTarget
        const issueDesc = form.IssueDesc.value
        const selectedMember = form.selectedMember.value
        const date = form.date.value
        

        console.log("submitIssue")
        console.log(date)
        console.log(typeof date)

        var selectedDate = Date.parse(date)
        var currentDate = Date.parse(new Date().toISOString().split('T')[0])
        
        if(selectedDate < currentDate)
        {
            console.log("invalid date")
            setIssueDateValid(false)
            return
        }



        if (form.checkValidity() === false ) {
            event.preventDefault();
            event.stopPropagation();
            setFormValidated(true);
        }
   
        else
        {     
            console.log("add issue")
            console.log(projectJson._id,m_currentIssueID,issueDesc, date, userName, selectedMember,"In Progress")
            sc.addIssue(projectJson._id,m_currentIssueID,issueDesc, date, userName, selectedMember,"In Progress" , successAddIssue, failedAddIssue)
           
            setFormValidated(false);
            setAddIssue(false)


        }
        
    }





    //classes for tabs view
    const Issues = (issues) => {
        const [issueList, setIssueList] = useState([]);
        const IssuesRow = ({id, desc, dueData, assigned, status}) => {
            let button
            if (status === "active"  ) {
                button =  <Button variant="success" >Complete</Button>
            } else {
                button =    <Button variant="secondary" disabled>Waiting Approval</Button>
            }

            return (    
                    <tr>
                        <td>{id}</td>
                        <td>{desc}</td>
                        <td>{dueData}</td>
                        <td>{assigned}</td>
                        <td>
                            {button}
                        </td>
                    </tr>
            )}
        return (    
            <div >
                <Table striped bordered hover>
                    <thead>
                        <tr>
                        <th>ID</th>
                        <th>Description</th>
                        <th>Due Date</th>
                        <th>Assigned By</th>
                        <th>        </th>
                        </tr>
                    </thead>
                    <tbody>
                    {issueList.map(({ _id,desc, dueData, assigned, status}) => (
                        <IssuesRow id = {_id} desc = {desc} dueData = {dueData} assigned={assigned} status={status}></IssuesRow>
                    ))}
                    
                    </tbody>
                    </Table>
            </div>
            
            
    
        )}
       

    const Completed = () => {
        return (    
            <div className="home" >
                <div className="labelStyle" >Completed: </div>  
            </div>
            
    
        )}
    const Overdue = () => {
        return (    
            <div className="home" >
                <div className="labelStyle" >Overdue: </div>  
            </div>
            
    
        )}

    const Members = () => {
        const MembersRow = ({name, premission}) => {
            let button
            if (premission === "Admin"  ) {
                button =  <Button variant="primary" disabled>Admin</Button>
            } else {
                button =  <Button variant="secondary" disabled>Member</Button>
            }

            return (    
                    <tr>
                        <td>{name}</td>
                        <td>
                            {button}
                        </td>
                    </tr>
            )}
        return (    
            <div >
                <Table striped bordered hover>
                    <thead>
                        <tr>
                        <th>User Name</th>
                        <th>Premission</th>
                        </tr>
                    </thead>
                    <tbody>
                    {m_adminList.map((name) => (
                        <MembersRow name = {name} premission = "Admin"></MembersRow>
                    ))}
                    {m_memberList.map((name) => (
                        <MembersRow name = {name} premission = "Member"></MembersRow>
                    ))}
                    
                    </tbody>
                    </Table>
            </div>
    )}

    const Waiting = () => {
        return (    
            <div className="home" >
                <div className="labelStyle" >Waiting: </div>  
            </div>
            
    
        )}


    //render ProjectWindow
    return (  

        <div className='center'>
        <div>
            <div className="labelStyle" >Project: {projectJson.projectName} </div>
            <button type="button" class="btn-addIssue" onClick={() => setAddIssue(true)}></button>
            { isAdmin ? <button type="button" class="btn-addMember" onClick={() => setAddMember(true)}></button>: null }
            <hr></hr>  
        </div>
            <Tabs defaultActiveKey="issues" id="uncontrolled-tab-example" className="mb-3">
            <Tab eventKey="issues" tabClassName="profile-issueTab" title="Issues" >
                <Issues />   
            </Tab>
            <Tab eventKey="completed" tabClassName="profile-completeTab" title="Completed">
                <Completed />      
            </Tab>
            <Tab eventKey="overdue" tabClassName="profile-overdueTab" title="Overdue">
                <Overdue />  
            </Tab>
            <Tab eventKey="members" tabClassName="profile-membersTab" title="Members">
                <Members />  
            </Tab>
            { isAdmin ? <Tab eventKey="waiting" tabClassName="profile-waitingTab"  title="Pending">
                <Waiting />  
            </Tab> : null }
            

            </Tabs>
             {/* Adding Issue Form*/}
             <Modal size="lg" aria-labelledby="contained-modal-title-vcenter" centered show={showAddIssue} onHide={()=>setAddIssue(false)}>
                <Form noValidate validated={formValidated} onSubmit={submitIssue}>
                    <Modal.Header closeButton>
                        <Modal.Title>Add Issue</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>

                        {/* Issue */}
                        <Form.Group controlId="IssueDesc">
                            <Form.Label>Issue Description</Form.Label>
                            <Form.Control required as="textarea" rows={3} placeholder="Write something..."/>   
                            <Form.Control.Feedback type="valid"></Form.Control.Feedback>
                            <Form.Control.Feedback type="invalid">
                            Please enter an issue description.
                            </Form.Control.Feedback>
                        </Form.Group>

                    
                        <Form.Group className="mb-3" controlId="selectedMember">
                        <Form.Label>Assign To:</Form.Label>
                        <Form.Control as="select">
                        {
                            isAdmin? 
                            dropDownItems.map((name) => (
                                <option value={name} >{name} </option>
                            ))
                            :   
                            <option value={userName} >{userName} </option>
                            
                        }
                        </Form.Control>

                    </Form.Group>
                    <Form.Group controlId="date">
                        <Form.Label> Due Date:</Form.Label>
                        <Form.Control type="date"  defaultValue= {new Date().toISOString().split('T')[0]}
                        isValid = {issueDateValid}
                        isInvalid = {! issueDateValid}/>
                        <Form.Control.Feedback type="valid"></Form.Control.Feedback>
                        <Form.Control.Feedback type="invalid">
                        Selected date is not valid.
                        </Form.Control.Feedback>
                    </Form.Group>
              
                       

                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={()=>setAddIssue(false)}>
                            Close
                        </Button>
                        <Button variant="primary" type="submit" >
                            Add
                        </Button>

                    </Modal.Footer>
                </Form>
            </Modal>


            {/* Adding Member Form*/}
            <Modal size="lg" aria-labelledby="contained-modal-title-vcenter" centered show={showAddMember} onHide={()=>setAddMember(false)}>
                <Form noValidate validated={formValidated} onSubmit={submitMember}>
                    <Modal.Header closeButton>
                        <Modal.Title>Add Member</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>

                        
                        <Form.Group controlId="memberUserName">
                            <Form.Label>User Name</Form.Label>
                            <Form.Control required type="text" placeholder={"Insert Name"} />
                            <Form.Control.Feedback type="valid"></Form.Control.Feedback>
                            <Form.Control.Feedback type="invalid">
                            Please enter a user name.
                            </Form.Control.Feedback>

                            <Form.Label>Choose Premissions</Form.Label>
                        </Form.Group>
                        <Dropdown>

                        <Dropdown.Toggle variant="outline-secondary" id="dropdown-basic">
                        {newMemberPremission}
                        </Dropdown.Toggle>

                        <Dropdown.Menu>
                            <Dropdown.Item href="#/action-1" onClick = {() => setPremmisoin("Member")}>Member</Dropdown.Item>
                            <Dropdown.Item href="#/action-2" onClick = {() => setPremmisoin("Admin")}>Admin</Dropdown.Item>
                        </Dropdown.Menu>
                        </Dropdown>

                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={closeMemberForm}>
                            Close
                        </Button>
                        <Button variant="primary" type="submit" >
                            Add
                        </Button>

                    </Modal.Footer>
                </Form>
            </Modal>
        </div>

    )
}



export default ProjectWindow
