import { Table,Card, Form, Button, Container, Row, Col, Badge, Alert, Tab, Tabs, ListGroup, Accordion, Modal ,Dropdown} from 'react-bootstrap'
import React, { list,useRef,useState, useEffect ,Sonnet ,render} from 'react';


const sc=require("../services/serverCom.js");

var adminFound = false
const ProjectWindow = ({userName, projectJson}) => {

    console.log("ProjectWindow:")
    console.log(userName)
    console.log(projectJson)


    //members
    const [formValidated, setFormValidated] = useState(false);
    const [showAddIssue, setAddIssue] = useState(false);
    const [showAddMember, setAddMember] = useState(false);
    const [newMemberPremission, setPremmisoin] = useState("Member");

    
    for (var index in projectJson.admin)
    {
        console.log(projectJson.admin[index])
        if(projectJson.admin[index] === userName)
        {
            adminFound = true
            console.log("found")
            break;
        }
    }
    
    const [isAdmin, setIsAdmin] = useState(Boolean(adminFound));
    
    //functions
    const addMemberToDB = (memberUserName) =>{
        //TODO 
        // 1) check if such userName exists in Users collection
        // 2) if (1) is valid then add the user name to the project member according to its premission
    }
    const closeMemberForm = () =>
    {
        setFormValidated(false);
        setPremmisoin("Member")
        setAddMember(false)
    }
    const submitMember = (event) =>{
        event.preventDefault();
        const form = event.currentTarget
        const memberUserName = form.memberUserName.value

        console.log("submitMember")
        console.log(memberUserName);
        console.log(newMemberPremission);

        if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
            setFormValidated(true);
        }
   
        else
        {     
            addMemberToDB(memberUserName)
            closeMemberForm()

        }
        
    }






    const submitIssue = () =>{

    }
    

    //classes for tabs view
    const Issues = (issues) => {
        const [issueList, setIssueList] = useState([]);
        const IssuesRow = ({id, desc, dueData, assigned, status}) => {
            let button
            if (status === "active"  ) {
                button =  <Button variant="success" >Complete</Button>
            } else {
                button =    <Button variant="secondary" disabled>Waiting Approval</Button>
            }

            return (    
                    <tr>
                        <td>{id}</td>
                        <td>{desc}</td>
                        <td>{dueData}</td>
                        <td>{assigned}</td>
                        <td>
                            {button}
                        </td>
                    </tr>
            )}
        return (    
            <div >
                <Table striped bordered hover>
                    <thead>
                        <tr>
                        <th>ID</th>
                        <th>Description</th>
                        <th>Due Date</th>
                        <th>Assigned By</th>
                        <th>        </th>
                        </tr>
                    </thead>
                    <tbody>
                    {issueList.map(({ _id,desc, dueData, assigned, status}) => (
                        <IssuesRow id = {_id} desc = {desc} dueData = {dueData} assigned={assigned} status={status}></IssuesRow>
                    ))}
                    
                    </tbody>
                    </Table>
            </div>
            
            
    
        )}
       

    const Completed = () => {
        return (    
            <div className="home" >
                <div className="labelStyle" >Completed: </div>  
            </div>
            
    
        )}
    const Overdue = () => {
        return (    
            <div className="home" >
                <div className="labelStyle" >Overdue: </div>  
            </div>
            
    
        )}

    const Members = () => {
        return (    
            <div className="home" >
                <div className="labelStyle" >Members: </div>  
            </div>
            
    
        )}

    const Waiting = () => {
        return (    
            <div className="home" >
                <div className="labelStyle" >Waiting: </div>  
            </div>
            
    
        )}


    //render ProjectWindow
    return (  
          
        <div className='center'>
        <div>
            <div className="labelStyle" >Project: {projectJson.projectName} </div>
            <Button  className="btn-addIssue" onClick={() => setAddIssue(true)}></Button>
            { isAdmin ?<Button variant="outline-primary" className="btn-addMember"  onClick={() => setAddMember(true)}></Button>: null }
            <hr></hr>  
        </div>
            <Tabs defaultActiveKey="issues" id="uncontrolled-tab-example" className="mb-3">
            <Tab eventKey="issues" tabClassName="profile-issueTab" title="Issues" >
                <Issues />   
            </Tab>
            <Tab eventKey="completed" tabClassName="profile-completeTab" title="Completed">
                <Completed />      
            </Tab>
            <Tab eventKey="overdue" tabClassName="profile-overdueTab" title="Overdue">
                <Overdue />  
            </Tab>
            <Tab eventKey="members" tabClassName="profile-membersTab" title="Members">
                <Members />  
            </Tab>
            { isAdmin ? <Tab eventKey="waiting" tabClassName="profile-waitingTab"  title="Pending">
                <Waiting />  
            </Tab> : null }
            

            </Tabs>
             {/* Adding Issue Form*/}
             <Modal size="lg" aria-labelledby="contained-modal-title-vcenter" centered show={showAddIssue} onHide={()=>setAddIssue(false)}>
                <Form noValidate validated={formValidated} onSubmit={submitIssue}>
                    <Modal.Header closeButton>
                        <Modal.Title>Add Issue</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>

                        {/* ProjectName */}
                        <Form.Group controlId="projectName">
                            <Form.Label>Projcet Name</Form.Label>
                            <Form.Control required type="text" placeholder={"Insert Name"} />
                            <Form.Control.Feedback type="valid"></Form.Control.Feedback>
                            <Form.Control.Feedback type="invalid">
                            Please enter an issue name.
                            </Form.Control.Feedback>
                        </Form.Group>

                        {/* Type of member */}
                        <Form.Group controlId="projectDescription">
                            <Form.Label>Description</Form.Label>
                            <Form.Control as="textarea" rows={3} placeholder="Description" />   
                        </Form.Group>

                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={()=>setAddIssue(false)}>
                            Close
                        </Button>
                        <Button variant="primary" type="submit" >
                            Add
                        </Button>

                    </Modal.Footer>
                </Form>
            </Modal>


            {/* Adding Member Form*/}
            <Modal size="lg" aria-labelledby="contained-modal-title-vcenter" centered show={showAddMember} onHide={()=>setAddMember(false)}>
                <Form noValidate validated={formValidated} onSubmit={submitMember}>
                    <Modal.Header closeButton>
                        <Modal.Title>Add Member</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>

                        
                        <Form.Group controlId="memberUserName">
                            <Form.Label>User Name</Form.Label>
                            <Form.Control required type="text" placeholder={"Insert Name"} />
                            <Form.Control.Feedback type="valid"></Form.Control.Feedback>
                            <Form.Control.Feedback type="invalid">
                            Please enter a user name.
                            </Form.Control.Feedback>

                            <Form.Label>Choose Premissions</Form.Label>
                        </Form.Group>
                        <Dropdown>

                        <Dropdown.Toggle variant="outline-secondary" id="dropdown-basic">
                        {newMemberPremission}
                        </Dropdown.Toggle>

                        <Dropdown.Menu>
                            <Dropdown.Item href="#/action-1" onClick = {() => setPremmisoin("Member")}>Member</Dropdown.Item>
                            <Dropdown.Item href="#/action-2" onClick = {() => setPremmisoin("Admin")}>Admin</Dropdown.Item>
                        </Dropdown.Menu>
                        </Dropdown>

                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={closeMemberForm}>
                            Close
                        </Button>
                        <Button variant="primary" type="submit" >
                            Add
                        </Button>

                    </Modal.Footer>
                </Form>
            </Modal>
        </div>
        

    )}



export default ProjectWindow
