import { Card, Form, Button, Container, Row, Col, Badge, Alert, Tab, ListGroup, Accordion, Modal } from 'react-bootstrap'
import React, { useState, useEffect } from 'react';
//ref to Todo.js -> ListsComponent
const ProjectsList = () => {
    return (
        <Container className='ProjctsGrid'>
            <Col>
                <Row xs={1} md={2} className="g-4">
                {Array.from({ length: 4 }).map((_, idx) => (
                    <Col>
                    <Card >
                    <Card.Body>
                        <Card.Title>Project Title</Card.Title>
                        <Card.Text>
                            Project Description
                        </Card.Text>
                        <Button variant="primary">Open</Button>
                    </Card.Body>
                    </Card>
                    </Col>
                ))}
                </Row>
            </Col>
        </Container>
    );
}

const ProjectDetails = () => {
    const EmptyProjct = { text: "", desc: "", date: "1111-11-11" }
    const [selectedProject, setSelectedProject] = useState(EmptyProjct);
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    
    return (

        <>
        {/* Model is pop-up for editing task details */}
            <Modal show={show} onHide={handleClose}>
                <Form onSubmit={onSubmit}>
                    <Modal.Header closeButton>
                        <Modal.Title>Task Details</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>

                        <Form.Group controlId="task">
                            <Form.Label>Task</Form.Label>
                            <Form.Control type="text" defaultValue={selectedTask.text} />
                        </Form.Group>
                        <Form.Group controlId="taskdesc">
                            <Form.Label>Description</Form.Label>
                            <Form.Control as="textarea" rows={3} defaultValue={selectedTask.desc} />
                        </Form.Group>

                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                        <Button variant="primary" type="submit">
                            Save Changes
                        </Button>

                    </Modal.Footer>
                </Form>
            </Modal>
        </>
    )
}

