import { Table,Card, Form, Button, Container, Row, Col, Badge, Alert, Tab, Tabs, ListGroup, Accordion, Modal } from 'react-bootstrap'
import React, { list,useRef,useState, useEffect ,Sonnet } from 'react';
const sc=require("../services/serverCom.js");


const ProjectWindow = ({ projectJson}) => {





    //classes for tabs view
    const Issues = () => {
        return (    
            <div >
                <Table striped bordered hover>
                    <thead>
                        <tr>
                        <th>ID</th>
                        <th>Description</th>
                        <th>Due Date</th>
                        <th>Assigned By</th>
                        <th>        </th>
                        </tr>
                    </thead>
                    <tbody>
                    </tbody>
                    </Table>
            </div>
            
            
    
        )}
        const IssuesRow = (id, desc, dueData, assigned, status) => {
            return (    
                    <tr>
                        <td>{id}</td>
                        <td>{desc}</td>
                        <td>{dueData}</td>
                        <td>{assigned}</td>
                        <td>
                            <Button variant="success" >Complete</Button>
                        </td>
                    </tr>
            )}

    const Completed = () => {
        return (    
            <div className="home" >
                <div className="labelStyle" >Completed: </div>  
            </div>
            
    
        )}
    const Overdue = () => {
        return (    
            <div className="home" >
                <div className="labelStyle" >Overdue: </div>  
            </div>
            
    
        )}

    const Members = () => {
        return (    
            <div className="home" >
                <div className="labelStyle" >Members: </div>  
            </div>
            
    
        )}

    const Waiting = () => {
        return (    
            <div className="home" >
                <div className="labelStyle" >Waiting: </div>  
            </div>
            
    
        )}


    return (    
        <div className='center'>
        <div className="home" >
            <div className="labelStyle" >Project: {projectJson.projectName}</div>
        </div>
            <Tabs defaultActiveKey="profile" id="uncontrolled-tab-example" className="mb-3">
            <Tab eventKey="issues" title="Issues">
                <Issues />   
            </Tab>
            <Tab eventKey="completed" title="Completed">
                <Completed />      
            </Tab>
            <Tab eventKey="overdue" title="Overdue">
                <Overdue />  
            </Tab>
            <Tab eventKey="members" title="Members">
                <Members />  
            </Tab>
            <Tab eventKey="waiting" title="Waiting for approval">
                <Waiting />  
            </Tab>

            </Tabs>
        </div>
        

    )}



export default ProjectWindow
