            }

            return (    
                    <tr>
                        <td>{userName}</td>
                        <td>
                            {button}
                        </td>
                    </tr>
            )}
        return (    
            <div >
                <Table striped bordered hover>
                    <thead>
                        <tr>
                        <th>User Name</th>
                        <th>Premission</th>
                        </tr>
                    </thead>
                    <tbody>
                    {m_adminList.map((userName) => (
                        <MembersRow userName = {userName} premission = "Admin"></MembersRow>
                    ))}
                     {m_memberList.map(({ _id,desc, dueData, assigned, status}) => (
                        <MembersRow userName = {userName} premission = "Member"></MembersRow>
                    ))}
                    
                    </tbody>
                    </Table>
            </div>
    )}

    const Waiting = () => {
        return (    
            <div className="home" >
                <div className="labelStyle" >Waiting: </div>  
            </div>
            
    
        )}


    //render ProjectWindow
    return (  

        <div className='center'>
        <div>
            <div className="labelStyle" >Project: {projectJson.projectName} </div>
