import { Card, Form, Button, Container, Row, Col, Badge, Alert, Tab, ListGroup, Accordion, Modal } from 'react-bootstrap'
import React, { list,useRef,useState, useEffect } from 'react';
const sc=require("../services/serverCom.js");

//ref to Todo.js -> ListsComponent
let global_setProjcetList
const Home = ({ userName }) => {


    const [formValidated, setFormValidated] = useState(false);
    const [m_userName, setUserName] = useState(userName)
    const [show, setShow] = useState(false);

    //TODO read list form database

    const [projectsList, setProjcetList] = useState([]);

    sc.getUserProjects(userName,updateUserProjectList);

    const updateUserProjectList = (projectJson)=>{
         
    }
  
    const onAddProjectSuccess = (projectJson) =>{
        console.log(projectJson)

        //add project to projects list
        const newList = [...projectsList, {name: String(projectJson.projectName), Description: String(projectJson.projectDesc)}];
        console.log(newList);
        setProjcetList(newList);
        console.log(projectsList);
    }

    const onAddProjectFailed = () =>{
        //Notify User
        console.log("project failed to add")
        alert('such project already exists!');
    }

    const handleClose = () => 
    {
        setFormValidated(false);
        setShow(false);
    }
    const handleShow = () => setShow(true);

    const addProject = () => 
    {
        handleShow();
    }

    const addProjectToDB = (user,projectName, projectDesc) =>{
        console.log("addProjectToDB")
        sc.addProject(user,projectName,projectDesc,projectsList, onAddProjectSuccess, onAddProjectFailed);
    }




    const submitProject = (event)=>
    {
   
        event.preventDefault();
        const form = event.currentTarget
        const projectName = form.projectName.value
        const projectDesc = form.projectDescription.value
        if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
            setFormValidated(true);
        }
   
        else
        {     
            handleClose();


            addProjectToDB(userName,projectName, projectDesc)

            //add project to dataBase
            

        }

  
    }
    //read projcets of user
    return (    
        <div >
        <div className="home">
            <div className="labelStyle">Hello, {m_userName}</div>
        </div>
        <div className="center">
            <div>
                <div id="projectName" className="labelStyle">My Projects</div>
                <Button variant="outline-success" onClick={addProject}>Add Project</Button>
                <hr></hr>
                
            </div>

            {/* Projcets Grid*/}
            <Container className='ProjctsGrid'>  
                {projectsList.map(({ name, Description,index }) => (
                    <Row>
                    <Col>
                        <Card>
                            <Card.Body>
                                <Card.Title>{name}</Card.Title>
                                <Card.Text>
                                {Description}
                                </Card.Text>
                                <Button variant="primary">Open</Button>
                            </Card.Body>
                        </Card>
                    </Col>
                    </Row> 
                ))}    
            </Container>


            {/* Adding Projcet Form*/}
            <Modal size="lg" aria-labelledby="contained-modal-title-vcenter" centered show={show} onHide={handleClose}>
                <Form noValidate validated={formValidated} onSubmit={submitProject}>
                    <Modal.Header closeButton>
                        <Modal.Title>Add Project</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>

                        {/* ProjectName */}
                        <Form.Group controlId="projectName">
                            <Form.Label>Projcet Name</Form.Label>
                            <Form.Control required type="text" placeholder={"Insert Name"} />
                            <Form.Control.Feedback type="valid"></Form.Control.Feedback>
                            <Form.Control.Feedback type="invalid">
                            Please enter a project description.
                            </Form.Control.Feedback>
                        </Form.Group>

                        {/* Description */}
                        <Form.Group controlId="projectDescription">
                            <Form.Label>Description</Form.Label>
                            <Form.Control as="textarea" rows={3} placeholder="Description" />   
                        </Form.Group>

                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                        <Button variant="primary" type="submit" >
                            Add
                        </Button>

                    </Modal.Footer>
                </Form>
            </Modal>
            
        </div>
        </div>     
    )}
export default Home
