import { Card, Form, Button, Container, Row, Col, Badge, Alert, Tab, ListGroup, Accordion, Modal } from 'react-bootstrap'
import React, { useRef,useState, useEffect } from 'react';








//ref to Todo.js -> ListsComponent
const ProjectsList = () => {
    function add2List(projectName){
        const newList = [...InputLists, { name: projectName, tasks: [] }];
        setInputLists(newList);
    }
    return (
        <Col sm={size}>
            <ListGroup>
                <TaskAdder addTask={addList} size={"sm"} name={"list"} />
                {InputLists.map((list, index) => <ListGroup.Item onClick={() => setList(list)} action href={`#${list.name}_${index}`}> {`${list.name}`} </ListGroup.Item>)}

            </ListGroup>
        </Col>
    );
}


const Home = ({ userName }) => {


    const [formValidated, setFormValidated] = useState(false);
    const [m_userName, setUserName] = useState(userName)
    const [show, setShow] = useState(false);
    const [projectName, setProjectName] = useState("");
    const handleClose = () => 
    {
        setFormValidated(false);
        setShow(false);
    }
    const handleShow = () => setShow(true);

    const addProject = () => 
    {
        handleShow();
    }

    const submitProject = (event)=>
    {
   
        event.preventDefault();
        const form = event.currentTarget
        const title = form.projectName.value
        const desc = form.projectDescription.value
        if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
            setFormValidated(true);
        }
   
        else
        {     
            handleClose();
            //add project to list 
            //add project to data base
        }

  
    }
    //read projcets of user
    return (    
        <div >
        <div className="home">
            <div className="labelStyle">Hello, {m_userName}</div>
        </div>
        <div className="center">
            <div>
                <div id="projectName" className="labelStyle">My Projects</div>
                <Button variant="outline-success" onClick={addProject}>Add Project</Button>
                <hr></hr>
                
            </div>
         
            <ProjectsList ></ProjectsList>

            <Modal size="lg" aria-labelledby="contained-modal-title-vcenter" centered show={show} onHide={handleClose}>
                <Form noValidate validated={formValidated} onSubmit={submitProject}>
                    <Modal.Header closeButton>
                        <Modal.Title>Task Details</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>

                        {/* ProjectName */}
                        <Form.Group controlId="projectName">
                            <Form.Label>Projcet Name</Form.Label>
                            <Form.Control required type="text" placeholder={"Insert Name"} />
                            <Form.Control.Feedback type="valid"></Form.Control.Feedback>
                            <Form.Control.Feedback type="invalid">
                            Please enter a project description.
                            </Form.Control.Feedback>
                        </Form.Group>

                        {/* Description */}
                        <Form.Group controlId="projectDescription">
                            <Form.Label>Description</Form.Label>
                            <Form.Control as="textarea" rows={3} placeholder="Description" />   
                        </Form.Group>

                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                        <Button variant="primary" type="submit" onclick={submitProject}>
                            Save Changes
                        </Button>

                    </Modal.Footer>
                </Form>
            </Modal>
        </div>
        </div>     
    )}
export default Home
